mapper.select:
SELECT COUNT(*)
FROM users
WHERE user_id = #{id}

mapper.update:
UPDATE users 
SET user_pw = #{userPw}, user_name = #{userName}, user_phone1 = #{userPhone1}, user_phone2 = #{userPhone2},
 user_email1 = #{userEmail1}, user_email2 = #{userEmail2},
  addr_basic = #{addrBasic}, addr_detail = #{addrDetail}, addr_zip_num = #{addrZipNum}
WHERE user_id = #{userId}
